import React from 'react';
import './SortController.css'
import AnimationSpeed from './AnimationSpeed/AnimationSpeed';

const SortController = (props)=>{
        return(<div className = "nav">
                    <button className = "reset" onClick = {props.clicked}>Reset Array</button>
                    <AnimationSpeed changed={props.changed} initual={props.initual} />
                    <input
                    id="changeSize"
                    type="range"
                    min="0"
                    max="100"
                    style={{background: ''}}
                    onChange={props.sliderchange}
                    />
                    <div >
                        <button className = "nav-item" onClick = {props.BubbleSortclicked}>Buble Sort</button>
                        <button className = "nav-item" onClick = {props.InsertonSortclicked}>Insertion Sort</button>
                        <button className = "nav-item" onClick = {props.SelectionSortclicked}>Selection Sort</button>
                        <button className = "nav-item" onClick = {props.MergeSortclicked}>Merge Sort</button>
                        <button className = "nav-item" onClick = {props.QuickSortclicked}>Quick Sort</button>

            </div>
        </div> 
        )
}

export default SortController;
