import React,{Component} from 'react';
import './SortBuilder.css'
import ResetArray from '../SortVisualization/ResetArray'
import SortController from '../SortController/SortController';
import BubbleSortAnimations from '../SortVisualization/BubleSortAnimation'
class SortBuilder extends Component{

state = {
    array:[]
}
totalWidth = window.innerWidth;
totalHeight = window.innerHeight-200;
noOfBars = parseInt((this.totalWidth-600)/4);
animationSpeed = 1

componentDidMount(){
    this.ResetArray();
}
ResetArray = ()=>{
    const RandArray = ResetArray(this.totalHeight,this.noOfBars)
    this.setState({array:RandArray})
}
BubbleSort = () => {
    const sortedaArray = BubbleSortAnimations(this.state.array,this.animationSpeed);
}
render(){
    const array = this.state.array;
    return(
        <div>
            <SortController 
            clicked = {this.ResetArray}
            BubbleSortclicked = {this.BubbleSort}
            />
            <div className="container">
                {array.map((value,index)=>(
                <div className="bar" key={index}
                style={{height:`${value}px`}}></div>
                ))}
            </div>
        </div>

    )
}
}

export default SortBuilder;
