import React,{Component} from 'react';
import './SortBuilder.css'
import ResetArray from '../../Components/SortVisualization/ResetArray'
import SortController from '../../Components/SortController/SortController';
import BubbleSortAnimation from '../../Components/SortVisualization/BubleSortAnimation'
import SelectionSortAnimation from '../../Components/SortVisualization/SelectionSortAnimation'
import InsertionSortAnimation from '../../Components/SortVisualization/SelectionSortAnimation'
import MergesortAnimation from '../../Components/SortVisualization/MergeSortAnimations'
import QuickSortAnimation from '../../Components/SortVisualization/QuickSortAnimation'
import EndAnimation from   '../../Components/SortVisualization/endAnimations';
import AnimationSpeed from '../../Components/SortController/AnimationSpeed/AnimationSpeed';
class SortBuilder extends Component{

state = {
    array:[],
    animationSpeed:0.1
}
totalWidth = window.innerWidth;
totalHeight = window.innerHeight;
noOfBars = parseInt((this.totalWidth-600)/4);
// animationSpeed = 0.1
primarycolor = "aqua"
secondarycolor= "red"

componentDidMount(){
    this.ResetArray();
}
ResetArray = ()=>{
    const RandArray = ResetArray(this.totalHeight,this.noOfBars)
    this.setState({array:RandArray})
}
BubbleSort = () => {
    const [sortedaArray,animationsLength] = BubbleSortAnimation(this.state.array,this.state.animationSpeed,this.primarycolor,this.secondarycolor);
    EndAnimation(sortedaArray,animationsLength,this.state.animationSpeed,this.primarycolor)
}
SelectionSort = () =>{
    const [sortedaArray,animationsLength] = SelectionSortAnimation(this.state.array,this.state.animationSpeed,this.primarycolor,this.secondarycolor);
    EndAnimation(sortedaArray,animationsLength,this.state.animationSpeed,this.primarycolor)
}

InsertionSort = () =>{
    const [sortedaArray,animationsLength] = InsertionSortAnimation(this.state.array,this.state.animationSpeed,this.primarycolor,this.secondarycolor);
    EndAnimation(sortedaArray,animationsLength,this.state.animationSpeed,this.primarycolor)
}

MegreSort = ()=>{
    const [sortedaArray,animationsLength] = MergesortAnimation(this.state.array,this.state.animationSpeed,this.primarycolor,this.secondarycolor);
    EndAnimation(sortedaArray,animationsLength,this.state.animationSpeed,this.primarycolor)
}

QuickSort = () =>{
    const [sortedaArray,animationsLength] = QuickSortAnimation(this.state.array,this.state.animationSpeed,this.primarycolor,this.secondarycolor);
    EndAnimation(sortedaArray,animationsLength,this.state.animationSpeed,this.primarycolor)
}

changedHandler = (event) =>{
    const newspeed = event.target.value
}
render(){
    const array = this.state.array;
    return(
        <div>
            <SortController 
            changed = {this.changedHandler}
            clicked = {this.ResetArray}
            BubbleSortclicked = {this.BubbleSort}
            SelectionSortclicked = {this.SelectionSort}
            InsertonSortclicked = {this.InsertionSort}
            MergeSortclicked = {this.MegreSort}
            QuickSortclicked = {this.QuickSort}
            />
            <AnimationSpeed />
            <div className="container">
                {array.map((value,index)=>(
                <div className="bar" key={index}
                style={
                    {height:`${value}px`,backgroundColor:this.primarycolor}
                }></div>
                ))}
            </div>
        </div>

    )
}
}

export default SortBuilder;
