import React,{Component} from 'react';
import './SortBuilder.css'
class SortBuilder extends Component{

state = {
    array:[]
}

CreateArray = ()=> {
    let randArray = []
    for(let i=0;i<200;i++){
        randArray.concat(Math.floor(Math.random()*100)+20);
    }
    this.setState({array:randArray});
}

render(){
    const array = this.state.array;
    return(
        <div>
        <div>ALl Intrustions and button</div>
        <div>
        {array.map(
            (value,index)=>{
                return(
                    <div key = {index} className = 'bar' style = {{height:`${value}px`}}></div>
                )
            }
        )}
        </div>

        </div>
    )
}
}

export default SortBuilder;
