import React,{Component} from 'react';
import './SortBuilder.css'
import ResetArray from '../SortVisualization/ResetArray'
import SortController from '../SortController/SortController';
class SortBuilder extends Component{

state = {
    array:[]
}
totalWidth = window.innerWidth;
totalHeight = window.innerHeight;
noOfBars = parseInt((this.totalWidth-600)/4);

componentDidMount(){
    this.ResetArray();
}
ResetArray = ()=>{
    const RandArray = ResetArray()
    this.state({array:RandArray})
}

render(){
    const array = this.state.array;
    return(
        <div>
            <SortController/>
            <div className="container">
                {array.map((value,index)=>(
                <div className="bar" key={index}
                style={{height:`${value}px`}}></div>
                ))}
    </div>
        </div>

    )
}
}

export default SortBuilder;
