import React,{Component} from 'react';
import './SortBuilder.css'
class SortBuilder extends Component{

state = {
    array:[]
}
totalWidth = window.innerWidth;
totalHeight = window.innerHeight;
noOfBars = parseInt((this.totalWidth-600)/4);

componentDidMount(){
    this.CreateArray();
}
CreateArray = ()=> {
    let randArray = []
    // console.log(this.totalHeight)
    // console.log(this.totalWidth)
    // console.log(this.noOfBars)
    for(let i=0;i<this.noOfBars;i++){
        randArray = randArray.concat(Math.floor(Math.random()*(this.totalHeight))+200);
    }
    this.setState({array:randArray});
}

render(){
    const array = this.state.array;
    return(
        <div className="container">
    {array.map((value,index)=>(
        <div className="bar" key={index}
        style={{height:`${value}px`}}></div>
        ))}
    </div>
    )
}
}

export default SortBuilder;
